    //Cnm=n!/m!*(n-m)!
    //Anm=n!/(n-m)! 5!/(5-3)!=60
    //排列   组合   123      123 132 213 231 321 312
    // A33=3!=1*2*3=6   A52=5!/3!=20
    // C33=1    C53=5!/(3!*2!)=10
    //存粹的组合  C53=5!/3!2! =10
    public static void one_c(List<String> list,int[] nums,String value,int len,int current){
        if (len==0){
            list.add(value);
            return;
        }else{
            for (int i=current;i<nums.length;i++){
                one_c(list,nums,value+nums[i],len-1,i+1);
            }
        }
    }
    //纯粹的排列A53=5!/(5-3)!=5!/2!=3*4*5=60
    public static void one_p(List<String> list,int[] nums,boolean[] flags,String value,int len){
        if (len==0){
            list.add(value);
            return;
        }else{
            for (int i=0;i<nums.length;i++){
                if (flags[i])continue;
                flags[i]=true;
                one_p(list,nums,flags,nums[i]+value,len-1);
                flags[i]=false;
            }
        }
    }

    public static void getAllSequence(List<String> list,int[] nums,boolean[] flags,String value,int current,int len){
        if (current==len){
            list.add(value);
            return;
        }else{
            for (int i=current;i<nums.length;i++){
                if (!flags[i]) {
                    flags[i]=true;
                    getAllSequence(list, nums, flags, value + nums[i],current + 1,len);
                    flags[i]=false;
                }
            }
        }
    }
    public static boolean find132pattern1(int[] nums){
        if (nums==null||nums.length<3)return false;
        int second=Integer.MIN_VALUE;
        int max=Integer.MIN_VALUE;
        for (int i=nums.length-1;i>=0;i--){
            if (nums[i]<second)return true;
            if (nums[i]>max){
                second=max;
                max=nums[i];
            }
        }
        return false;
    }
    //超时
    public static boolean find132pattern(int[] nums) {
        if (nums.length<3)return false;
        try {
            getAllSequence1(nums, new int[3], 3, 0, 0);
        }catch (Exception e){
            return true;
        }
        return false;
    }
    public static boolean is132Pattern(int[] nums){
        return nums[0]<nums[2]&&nums[2]<nums[1];
    }
    public static void getAllSequence1(int[] nums,int[] result,int len,int index,int current) throws Exception {
        if (len==0){
            if (is132Pattern(result)) {
                throw new Exception("error");
            }
        }else{
            for (int i=current;i<nums.length;i++){
                result[index]=nums[i];
                index=index+1;
                getAllSequence1(nums, result,len-1,index,i+1);
                index=index-1;
            }
        }
    }
